const wait = require('./wait');

/** animate object's {x, y} using an angle */
class angle extends wait
{
    /**
     * @param {object} object to animate
     * @param {number} angle in radians
     * @param {number} speed in pixels/millisecond
     * @param {number} [duration=0] in milliseconds; if 0, then continues forever
     * @param {object} [options] @see {@link Wait}
     */
    constructor(object, angle, speed, duration, options)
    {
        options = options || {};
        super(object, options);
        this.type = 'Angle';
        if (options.load)
        {
            this.load(options.load);
        }
        else
        {
            this.angle = angle;
            this.speed = speed;
            this.duration = duration || 0;
        }
    }

    save()
    {
        if (this.options.cancel)
        {
            return null;
        }
        const save = super.save();
        save.angle = this.angle;
        save.speed = this.speed;
        return save;
    }

    load(load)
    {
        super.load(load);
        this.angle = load.angle;
        this.speed = load.speed;
    }

    get angle()
    {
        return this._angle;
    }
    set angle(value)
    {
        this._angle = value;
        this.sin = Math.sin(this._angle);
        this.cos = Math.cos(this._angle);
    }

    calculate(elapsed)
    {
        this.object.x += this.cos * elapsed * this.speed;
        this.object.y += this.sin * elapsed * this.speed;
    }
}

module.exports = angle;

/**
 * @file src/animate.js
 * @author David Figatner
 * @license MIT
 * @copyright YOPEY YOPEY LLC 2016
 * {@link https://github.com/davidfig/animate}
 */

const _list = []
const _defaults = { ease: 'linear' }
let _lastTime = null

/**
 * initialize (may be called more than once to change options without changing animation list but don't pass Ticker more than once)
 * @param {object} [options]
 * @param {PIXI.Ticker} [options.ticker] attaches to the PIXI.Ticker; if this is not provided, you need to call update() manually
 * @param {function|string} [options.ease=linear] default easing function
 */
function init(options)
{
    _defaults.ease = options.ease || _defaults.ease
    if (options.ticker)
    {
        options.ticker.add(update)
    }
}

/**
 * remove an animation
 * @param {object|array} animate - the animation (or array of animations) to remove - can be null
 */
function remove(animate)
{
    if (animate)
    {
        if (Array.isArray(animate))
        {
            while (animate.length)
            {
                const pop = animate.pop()
                if (pop && pop.options)
                {
                    pop.options.cancel = true
                }
            }
        }
        else
        {
            if (animate && animate.options)
            {
                animate.options.cancel = true
            }
        }
    }
}

// internal function to add an animation
function add(animate)
{
    _list.push(animate)
    return animate
}

/**
 * update function
 * @param {number} elapsed since last update
 * @returns {number} of active animations
 */
function update()
{
    let elapsed
    if (_lastTime)
    {
        const time = window.performance.now()
        elapsed = time - _lastTime
        _lastTime = time
    }
    else
    {
        _lastTime = window.performance.now()
        return
    }
    let n = 0
    for (let i = _list.length - 1; i >= 0; i--)
    {
        const animate = _list[i]
        if (animate.update(elapsed))
        {
            _list.splice(i, 1)
        }
        else
        {
            if (!animate.options.pause)
            {
                n++
            }
        }
    }
    return n
}

module.exports = {
    init,
    update,
    remove,
    add,
    get defaults()
    {
        return _defaults;
    },
    wait: require('./wait'),
    to: require('./to'),
    shake: require('./shake'),
    tint: require('./tint'),
    face: require('./face'),
    angle: require('./angle'),
    target: require('./target'),
    movie: require('./movie'),
    load: require('./load')
}

/**
 * @file src/face.js
 * @author David Figatner
